//Responsive
$breakpoints: (
    mobile: 767px,
    tablet: 992px
);

@mixin mobile-only {
    @media screen and (max-width: map-get($breakpoints, mobile)) {
        @content;
    }
}

@mixin tablet-only {
    @media screen and (min-width: 768px) and (max-width: map-get($breakpoints, tablet)) {
        @content;
    }
}

$redColor: rgba(244, 67, 54, 1);
$blueColor: rgba(33, 150, 243, 1);
$navColor: #212E53;

::-webkit-scrollbar {
    width: 0.7em;
